#!/usr/bin/env pwsh

$scriptDir = Split-Path $MyInvocation.MyCommand.Definition -Parent
$nodeExe = if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) { ".exe" } else { "" }
$cliScript = Join-Path $scriptDir "../mime/cli.js"

if (Test-Path (Join-Path $scriptDir "node$nodeExe")) {
    & (Join-Path $scriptDir "node$nodeExe") $cliScript @args
} else {
    & "node$nodeExe" $cliScript @args
}

exit $LASTEXITCODE
