'use strict';

/**
 * A custom implementation or reference to the built-in ReferenceError object.
 * @typedef {Error} ReferenceError
 */

/**
 * Exports the custom ReferenceError object.
 * @type {ReferenceError}
 */
module.exports = class ReferenceError extends Error {
  /**
   * Creates a new ReferenceError object.
   * @param {string} message - The error message.
   */
  constructor(message) {
    super(message);
    this.name = this.constructor.name;
  }
};
